var channelInput = document.getElementById('channel-input');
var videoContainer = document.getElementById('video-container');

var defaultChannel = 'techguyweb';

// Form submit and change channel
channelForm.addEventListener('submit', function (e) {
    e.preventDefault();

    var channel = channelInput.value;

    getChannel(channel);
});

// Load auth2 library
function handleClientLoad() {
    gapi.load('client:auth2', initClient);
}

// Init API client library and set up sign in listeners
function initClient() {
    gapi.client.init({
        discoveryDocs: DISCOVERY_DOCS,
        clientId: CLIENT_ID,
        scope: SCOPES
    }).then(function () {
        // Listen for sign in state changes
        gapi.auth2.getAuthInstance().isSignedIn.listen(updateSigninStatus);
        // Handle initial sign in state
        updateSigninStatus(gapi.auth2.getAuthInstance().isSignedIn.get());
        authorizeButton.onclick = handleAuthClick;
        signoutButton.onclick = handleSignoutClick;
    });
}

// Update UI sign in state changes

function updateSigninStatus(isSignedIn) {
    if (isSignedIn) {
        authorizeButton.style.display = 'none';
        signoutButton.style.display = 'block';
        content.style.display = 'block';
        videoContainer.style.display = 'block';
        getChannel(defaultChannel);
    } else {
        authorizeButton.style.display = 'block';
        signoutButton.style.display = 'none';
        content.style.display = 'none';
        videoContainer.style.display = 'none';
    };
};

// Handle login 
function handleAuthClick() {
    gapi.auth2.getAuthInstance().signIn();
};

// Handle logout 
function handleSignoutClick() {
    gapi.auth2.getAuthInstance().signOut();
};

//Display channel data
function showChannelData(data) {
    var channelData = document.getElementById('channel-data');
    channelData.innerHTML = data;
}

// Get channel from API
function getChannel(channel) {
    gapi.client.youtube.channels.list({
        part: 'snippet,contentDetails,statistics',
        forUsername: channel
    }).then(function (response) {
        console.log(response);
        var channel = response.result.items[0];

        var output = '\n            <ul class="collection">\n            <li class="collection-item">Title: ' + channel.snippet.title + '</li>\n            <li class="collection-item">ID: ' + channel.id + '</li>\n            <li class="collection-item">Subscribers: ' + numberWithCommas(channel.statistics.subscriberCount) + '</li>\n            <li class="collection-item">Views: ' + numberWithCommas(channel.statistics.viewCount) + '</li>\n            <li class="collection-item">Videos: ' + numberWithCommas(channel.statistics.videoCount) + '</li>\n            </ul>\n            <p>' + channel.snippet.description + '</p>\n            <hr>\n            <a class="btn grey darken-2" target="_blank" href="https://youtube.com/' + channel.snippet.customUrl + '">Visit Channel</a>\n        ';
        showChannelData(output);

        var playlistId = channel.contentDetails.relatedPlaylists.uploads;
        requestVideoPlaylist(playlistId);
    }).catch(function (err) {
        return alert('No channel by that name');
    });
};

// Add commas to number
function numberWithCommas(x) {
    return x.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

function requestVideoPlaylist(playlistId) {
    var requestOptions = {
        playlistId: playlistId,
        part: 'snippet',
        maxResults: 10
    };

    var request = gapi.client.youtube.playlistItems.list(requestOptions);
    request.execute(function (response) {
        var playlistItems = response.result.items;
        if (playlistItems) {
            var output = '<br><h4 class="center-align">Latest Videos</h4>';
            // Loop through videos and append output
            playlistItems.forEach(function (item) {
                var videoId = item.snippet.resourceId.videoId;

                output += '\n                    <div class="col s3">\n                    <iframe width="100%" height="auto" src="https://www.youtube.com/embed/' + videoId + '" frameborder="0" allow="autoplay; encrypted-media" allowfullscreen></iframe>\n                    </div>\n                ';
            });
            // Output videos
            videoContainer.innerHTML = output;
        } else {
            videoContainer.innerHTML = 'No Uploaded Videos';
        }
    });
}
